while($elIdArr = $dbEl->Fetch()) {
    $arrId[] = $elIdArr['ID'];    
}


//собираем все со свойствами
$dbEl = CIBlockElement::GetList(array('sort' => 'asc'), array('IBLOCK_ID' => 40, ), false, false, Array('ID','PROPERTY_TOVAR_PO_AKCII',));
while($elIdArr = $dbEl->Fetch()) {  

$items[$elIdArr['ID']]['SET'] = (in_array($elIdArr['ID'],$arrId)?'Y':'N');
$items[$elIdArr['ID']]['TOVAR_PO_AKCII'][] = $elIdArr['PROPERTY_TOVAR_PO_AKCII_VALUE'];
}

//проверяем свойство на наличие уже отметок как старых так и новых, если текущая есть не трогаем нету добавляем, старые не затираем
foreach($items as $kl=>$vl){
    if(empty($vl['TOVAR_PO_AKCII']) &&  $vl['SET']=='N'){
        unset($items[$kl]);
    }elseif(empty($vl['TOVAR_PO_AKCII']) && $vl['SET']=='Y'){
        $items[$kl]['TOVAR_PO_AKCII'][]=$akcii_add_id;
    }elseif (!empty($vl['TOVAR_PO_AKCII']) && $vl['SET']=='N'){
        $key = array_search ($akcii_add_id, $vl['TOVAR_PO_AKCII']);
        if (false !== $key) {
            $items[$kl]['TOVAR_PO_AKCII'][$key] = '';
        }else{
            unset($items[$kl]);
        }
    }elseif (!empty($vl['TOVAR_PO_AKCII']) && $vl['SET']=='Y'){
        $key = array_search ($akcii_add_id, $vl['TOVAR_PO_AKCII']);
        if (false !== $key){
            unset($items[$kl]);
        }else{
            $items[$kl]['TOVAR_PO_AKCII'][]=$akcii_add_id;
        }
    }
}
